CREATE TABLE `products_category_sid` (
  `category_sid` int(11) NOT NULL AUTO_INCREMENT COMMENT '商品項目序號',
  `category` varchar(2) NOT NULL COMMENT '項目',
  PRIMARY KEY (`category_sid`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

CREATE TABLE `products_color_sid` (
  `product_color_sid` int(11) NOT NULL AUTO_INCREMENT COMMENT '商品顏色序號',
  `color` varchar(3) NOT NULL COMMENT '顏色',
  PRIMARY KEY (`product_color_sid`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8;

CREATE TABLE `products_group_sid` (
  `products_group_sid` int(11) NOT NULL AUTO_INCREMENT COMMENT '商品群組序號',
  `product_group` varchar(255) NOT NULL COMMENT '商品群組',
  PRIMARY KEY (`products_group_sid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE `products_size_sid_h` (
  `size_sid_h_sid` int(11) NOT NULL AUTO_INCREMENT COMMENT '尺寸規格編號(H)',
  `size` varchar(255) NOT NULL COMMENT '尺寸規格',
  PRIMARY KEY (`size_sid_h_sid`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

CREATE TABLE `products_size_sid_w` (
  `size_sid_w_sid` int(11) NOT NULL AUTO_INCREMENT COMMENT '尺寸規格編號(W)',
  `size` varchar(255) NOT NULL COMMENT '尺寸規格',
  PRIMARY KEY (`size_sid_w_sid`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;
